targets.rawSql:
SELECT COUNT(DISTINCT(temp_table.contents)) FROM (
SELECT unnest(string_to_array(content_ids, ',')) AS contents FROM content_validation_x WHERE $__timeFilter(created_at)
) AS temp_table;

SELECT temp_table.platform AS "Platform", COUNT(DISTINCT(temp_table.contents)) FROM (
SELECT platform, unnest(string_to_array(content_ids, ',')) AS contents FROM content_validation_x WHERE $__timeFilter(created_at)
) AS temp_table GROUP BY temp_table.platform;
