// Inicializar arrays para armazenar os dados das séries
const seriesData = [];

// Cores pré-definidas para as séries
const predefinedColors = [
  '#5470C6', '#91CC75', '#FAC858', '#EE6666', '#73C0DE',
  '#3BA272', '#FC8452', '#9A60B4', '#EA7CCC', '#8B8B8B'
];

// Índice para acompanhar a cor usada
let colorIndex = 0;

// Função para converter bytes para uma unidade legível
function convertBytes(value) {
  if (value >= 1e12) {
    return { value: (value / 1e12).toFixed(2), unit: 'TB' };
  } else if (value >= 1e9) {
    return { value: (value / 1e9).toFixed(2), unit: 'GB' };
  } else if (value >= 1e6) {
    return { value: (value / 1e6).toFixed(2), unit: 'MB' };
  } else if (value >= 1e3) {
    return { value: (value / 1e3).toFixed(2), unit: 'KB' };
  } else {
    return { value: value.toFixed(2), unit: 'B' };
  }
}

// Iterar pelos frames de dados do Grafana
context.panel.data.series.forEach((series, seriesIndex) => {
  // Encontrar os campos de valores numéricos
  const valueFields = series.fields.filter((field) => field.type === 'number');

  // Iterar sobre cada conjunto de valores numéricos
  valueFields.forEach((valueField, valueIndex) => {
    // Garantir que os nomes das séries sejam extraídos corretamente
    const seriesName = valueField.config.displayNameFromDS || valueField.name || `Series ${seriesIndex + 1}.${valueIndex + 1}`;

    // Mapear os dados de valores para o formato [valor, unidade]
    const data = valueField.values.map(d => {
      const converted = convertBytes(parseFloat(d));
      return {
        value: parseFloat(converted.value),
        unit: converted.unit
      };
    });

    // Adicionar a série de dados ao array de séries
    seriesData.push({
      name: seriesName,
      type: 'bar',
      barWidth: '40%', // Largura das barras (40% da categoria)
      barGap: '20%', // Espaço entre as barras (20% da largura da barra)
      itemStyle: {
        color: {
          type: 'linear',
          x: 0,
          y: 0,
          x2: 0,
          y2: 1,
          colorStops: [{
            offset: 0, color: predefinedColors[colorIndex] // Cor inicial
          }, {
            offset: 1, color: '#FFF' // Cor final (branco para simular degradação)
          }],
          globalCoord: false // Coordenação local para o gradiente
        },
        barBorderRadius: [50, 5, 0, 0] // Borda superior redonda
      },
      label: {
        show: true, // Mostrar o valor na barra
        position: 'top', // Posição do valor (acima da barra)
        color: 'white', // Cor do texto do valor (branco)
        fontWeight: 'bold', // Negrito no texto do valor
        formatter: function (params) {
          return `${params.value} ${params.data.unit}`; // Valor e unidade
        }
      },
      emphasis: {
        focus: 'series'
      },
      data: data
    });

    // Avançar para a próxima cor na lista de cores pré-definidas
    colorIndex = (colorIndex + 1) % predefinedColors.length;
  });
});

