const timeLabels = context.panel.data.series[0].fields
  .find(field => field.type === 'time')
  .values.toArray()
  .map(time => new Date(time).toLocaleTimeString());

// Retornar a configuração do gráfico
return {
  color: predefinedColors, // Cores das séries
  tooltip: {
    trigger: 'axis',
