    boundaryGap: true,
    data: timeLabels, // Rótulos de tempo para o eixo x
    axisLine: {
      lineStyle: {
        color: '#ccc'
      }
    },
    axisLabel: {
      textStyle: {
        color: '#ccc'
      }
    },
    splitLine: {
      show: false // Remover as linhas de fundo
    }
  }],
  yAxis: [{
    type: 'value',
    axisLine: {
      lineStyle: {
        color: '#ccc'
      }
    },
    axisLabel: {
      formatter: function (value) {
        const converted = convertBytes(value);
        return `${converted.value} ${converted.unit}`;
      },
      textStyle: {
        color: '#ccc'
      }
    },
    splitLine: {
      show: false // Remover as linhas de fundo
    }
  }],
  series: seriesData.map(s => ({
