        const seriesName = param.seriesName;
        const dataIndex = param.dataIndex;
        const data = seriesData.find(s => s.name === seriesName).data[dataIndex];
        tooltipText += `${seriesName}: ${data.value} ${commonUnit}<br/>`;
      });
      return tooltipText;
    }
  },
  legend: {
    data: seriesData.map(s => s.name)
  },
  toolbox: {
    orient: 'vertical',
    left: 'right',
    top: 'center',
    feature: {
      saveAsImage: {}
    }
  },
  grid: {
    left: '3%',
    right: '4%',
    bottom: '3%',
    containLabel: true
  },
  xAxis: [{
    type: 'category',
    boundaryGap: false,
    data: timeLabels,
    axisLine: {
      lineStyle: {
        color: '#ccc'
      }
    },
    axisLabel: {
      textStyle: {
        color: '#ccc'
      }
    },
    splitLine: {
      show: false // Remover as linhas de fundo
    }
  }],
  yAxis: [{
    type: 'value',
    axisLine: {
      lineStyle: {
        color: '#ccc'
      }
    },
    axisLabel: {
      formatter: function (value) {
        const formatted = formatMilliseconds(value);
        return `${formatted.value} ${commonUnit}`;
      },
      textStyle: {
        color: '#ccc'
      }
    },
    splitLine: {
      show: false // Remover as linhas de fundo
    }
  }],
  series: seriesData.map(s => ({
    ...s,
    data: s.data.map(d => ({
      ...d,
      value: d.value
    }))
  }))
};
